  dataFile = FileAttachment("data/combinedv10.csv").csv()









  Code
  parseDate = d3.timeParse("%Y-%m-%d")









  Code
  data = dataFile.map((d) =&gt; {
  return {
    ...d,
    Date: parseDate(d.Date)
  };
})









  Code
  d3 = require("d3@v6")









  Code
  beeswarm = (data, date, type) =&gt; {
  ////// Chart configuration
  const radius = 2.5;
  const padding = 1;
  const size = { w: 600, h: 800 };
  const margin = { top: 100, bottom: 30, left: 130, right: 100 };

  const colors = "#e6ab02";

  const xScale = d3
    .scaleTime()
    .range([0, size.w])
    .domain(d3.extent(data, (d) =&gt; d.Date));


  let category = [...new Set(data.map((d) =&gt; d[type]))];
  category.reverse();

  ////// Chart drawing
  const svg = d3.select(
    DOM.svg(
      size.w + margin.left + margin.right,
      size.h + margin.top + margin.bottom
    )
  );

  const g = svg
    .append("g")
    .attr("transform", `translate(${margin.left}, ${margin.top})`);

  // x axis
  const xAxis = d3.axisBottom().scale(xScale).ticks(d3.timeYear);
  g.append("g")
    .attr("transform", `translate(0, ${size.h - margin.top})`)
    .call(xAxis)
    .select(".domain")
    .remove();

  // y scale
  const yScale = d3
    .scalePoint()
    .domain(category)
    .range([0, size.h - margin.top - margin.bottom]);
  const yAxis = d3.axisLeft().scale(yScale);

  g.append("g")
    .attr("transform", `translate(0,0)`)
    .call(yAxis)
    .select(".domain")
    .remove();

  //Tooltip
  const toolTip = d3.select("body").append("div").attr("class", "toolTip");

  // mouseover function

  const formatDate = d3.timeFormat("%d %b %Y");

  function nodeMouseOver(event, d) {
    toolTip
      .style("left", event.pageX + 18 + "px")
      .style("top", event.pageY + 18 + "px")
      .style("display", "block")
      .html(
        `${d["Location"].toUpperCase()} - &lt;em&gt;${formatDate(
          d["Date"]
        )}&lt;/em&gt;&lt;br&gt;&lt;strong&gt;${d.Title}&lt;/strong&gt;&lt;br&gt;${d.Text.slice(0, 200)}...`
      );

    d3.select(event.target).style("cursor", "pointer");

    d3.select(event.target).transition().attr("fill", "#A8234E").attr("r", 10);
  }

  // mouseout function
  function nodeMouseOut(event, d) {
    // Hide tooltip on mouse out
    toolTip.style("display", "none"); // Hide toolTip

    d3.select(event.target).style("cursor", "default");

    d3.select(event.target)
      .transition()
      .attr("fill", "orange")
      .attr("r", radius);
  }

  // circles
  const bee = g
    .append("g")
    .attr("class", "bee")
    .attr("transform", `translate(0,0)`);

  const nodes = bee
    .selectAll("circle")
    .data(data)
    .enter()
    .append("circle")
    .attr("class", "node")
    .attr("cy", (d) =&gt; {
      d.y = size.h / 2;
      return d.y;
    })
    .attr("cx", (d) =&gt; {
      d.x = xScale(d[date]);
      return d.x;
    })
    .attr("r", radius)
    .attr("fill", "steelblue")
    .attr("fill-opacity", 0.7)
    .on("mousemove", nodeMouseOver)
    .on("mouseout", nodeMouseOut);

  let simulation = d3
    .forceSimulation()
    .force("collide", d3.forceCollide(radius + padding))
    .force("x", d3.forceX((d) =&gt; d.x).strength(1))
    .alphaMin(0.00001);

  if (category) {
    simulation.force("y", d3.forceY((d) =&gt; yScale(d[type])).strength(1));
  } else {
    simulation.force("y", d3.forceY((d) =&gt; d.y).strength(1));
  }

  simulation.nodes(data).on("tick", () =&gt; {
    nodes.attr("cx", (d) =&gt; d.x).attr("cy", (d) =&gt; d.y);
  });

  return svg.node();
}
